import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet],
  template: `
    <div class="container">
      <h1 style="text-align: center; margin-bottom: 30px; color: #333;">Task Management System</h1>
      <router-outlet></router-outlet>
    </div>
  `
})
export class AppComponent {
  title = 'task-list-app';
}
