export class TaskListComponent {
  constructor(taskService) {
    this.taskService = taskService;
    this.tasks = [];
    this.taskTypes = [];
    this.contactPersons = [];
    this.loading = false;
    this.showModal = false;
    this.editingTask = null;
    this.filters = {
      entity_name: '',
      task_type: '',
      status: '',
      contact_person: '',
      task_date: '',
      sort_by: 'date_created',
      sort_order: ''
    };
    this.taskForm = {
      entity_name: '',
      task_type: '',
      task_date: '',
      // Separate date field
      task_time_hour: '12',
      // Separate hour field for time picker
      task_time_minute: '00',
      // Separate minute field for time picker
      task_time_ampm: 'PM',
      // Separate AM/PM field for time picker
      contact_person: '',
      note: '',
      status: 'open'
      // Removed phone_number from taskForm
    };
    this.hours = Array.from({
      length: 12
    }, (_, i) => (i + 1).toString().padStart(2, '0'));
    this.minutes = ['00', '15', '30', '45'];
    this.alert = null;
  }
  ngOnInit() {
    this.loadTasks();
    this.loadTaskTypes();
    this.loadContactPersons();
  }
  loadTasks() {
    this.loading = true;
    this.taskService.getTasks(this.filters).subscribe({
      next: tasks => {
        this.tasks = tasks;
        this.loading = false;
      },
      error: error => {
        this.showAlert('error', 'Error loading tasks: ' + error.message);
        this.loading = false;
      }
    });
  }
  loadTaskTypes() {
